// === I18N ENGINE ===
let currentLang = 'en';

function setLanguage(lang) {
  const translations = T[lang] || T.en;
  currentLang = lang;

  // Update text content
  document.querySelectorAll('[data-i18n]').forEach(el => {
    const key = el.getAttribute('data-i18n');
    if (translations[key]) el.textContent = translations[key];
  });

  // Update HTML content (for elements with emojis, links, etc.)
  document.querySelectorAll('[data-i18n-html]').forEach(el => {
    const key = el.getAttribute('data-i18n-html');
    if (translations[key]) el.innerHTML = translations[key];
  });

  // Update HTML dir attribute for RTL
  const langInfo = LANGUAGES.find(l => l.code === lang);
  document.documentElement.dir = langInfo && langInfo.dir === 'rtl' ? 'rtl' : 'ltr';
  document.documentElement.lang = lang;

  // Update lang selector button
  if (langInfo) {
    document.getElementById('lang-flag').textContent = langInfo.flag;
    document.getElementById('lang-code').textContent = langInfo.label;
  }

  // Update active state in dropdown
  document.querySelectorAll('.lang-option').forEach(btn => {
    btn.classList.toggle('active', btn.dataset.lang === lang);
  });

  // Save preference
  try { localStorage.setItem('breakmate-lang', lang); } catch(e) {}

  // Update mascot animation labels
  if (window.updateMascotLabels) window.updateMascotLabels(translations);
}

function initI18n() {
  // Build dropdown
  const dropdown = document.getElementById('lang-dropdown');
  LANGUAGES.forEach(l => {
    const btn = document.createElement('button');
    btn.className = 'lang-option' + (l.code === 'en' ? ' active' : '');
    btn.dataset.lang = l.code;
    btn.innerHTML = l.flag + ' ' + l.name;
    btn.addEventListener('click', () => {
      setLanguage(l.code);
      dropdown.classList.remove('open');
    });
    dropdown.appendChild(btn);
  });

  // Toggle dropdown
  document.getElementById('lang-btn').addEventListener('click', (e) => {
    e.stopPropagation();
    dropdown.classList.toggle('open');
  });

  // Close on outside click
  document.addEventListener('click', () => dropdown.classList.remove('open'));
  dropdown.addEventListener('click', (e) => e.stopPropagation());

  // Detect saved language or browser language
  let savedLang = null;
  try { savedLang = localStorage.getItem('breakmate-lang'); } catch(e) {}
  if (savedLang && T[savedLang]) {
    setLanguage(savedLang);
  } else {
    const browserLang = navigator.language || '';
    const match = LANGUAGES.find(l => browserLang.startsWith(l.code.split('-')[0]));
    if (match && T[match.code]) setLanguage(match.code);
  }
}

// Start
initI18n();
